Ext.define('liftnet.controller.Engineer.EngReportController', {
    extend: 'Ext.app.Controller',

    config: {
        control: {
            "container#EngReport": {
                show: 'onEngReportShow'
            },
            "button#EngReport_ReturnButton": {
                tap: 'onEngReport_ReturnButtonTap'
            }
        }
    },

    onEngReportShow: function(component, eOpts) {
        // 热线受信报表
        loadMask.show();
        Ext.getCmp('EngReport_DvHotline').getStore().loadDat(loadMask.hide);

        // 保养计划报表
        loadMask.show();
        Ext.getCmp('EngReport_DvMaintain').getStore().loadDat(loadMask.hide);

        // 故障报告书报表
        loadMask.show();
        Ext.getCmp('EngReport_DvFault').getStore().loadDat(loadMask.hide);

        // 维保业绩报表
        loadMask.show();
        Ext.getCmp('EngReport_DvPerformance').getStore().loadDat(loadMask.hide);
    },

    onEngReport_ReturnButtonTap: function(button, e, eOpts) {
        viewUtil.goLast();
    }

});