Ext.define('liftnet.store.Common_repairmanlist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'Common_repairmanlist',
        fields: [
            {
                name: 'repairman'
            },
            {
                name: 'department'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/rpt/api/1.0/rptmanlist';
            var userid = global.getUserId();
            var adapterParm = {userid: userid};

            callPostAdapter(
                url, adapterParm,
                function(ret) {
                    me.setData(ret.data);
                    onSuccess(ret.data.length);
                },
                onFailure);
        } else {

            this.setData([
                {
                    repairman: '黄林',
                    department: '维保一组'
                },
                {
                    repairman: '黄林',
                    department: '维保五组'
                },
                {
                    repairman: '高诚修',
                    department: '维保业务科'
                },
                {
                    repairman: '郭守波',
                    department: '维保业务科'
                }
            ]);

            onSuccess();
        }
    }

});