Ext.define('liftnet.store.common_cominfolist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'common_cominfolist',
        fields: [
            {
                name: 'unitname',
                type: 'string'
            },
            {
                name: 'addr',
                type: 'string'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        console.log(parm);
        var me = this;
        if(global.isWorklight()) {// adapter取数
            // adapter取数
            var url = '/adapters/sx/api/1.0/cominfolist';
            callPostAdapter(
                url, parm,
                function(ret) {
                    var d = ret.data;
                    var n = d.length;
                    for(var i=0; i<n; i++) {
                        me.add(d[i]);
                    }
                    onSuccess(n);
                },
            onFailure);
        } else {
            var start = parm.pageno, end = parm.pageno + parm.pagelen - 1;
            end = end>58? 58:end;

            for(var i=start; i<=end; i++) {
                this.add({
                    unitname: '广州市华越友联科技发展有限公司.' + (i + 1),
                    addr: '广州市荔湾区黄沙大道144号大冶有色大厦1001室',
                });
            }
            // 演示数据
            onSuccess(end - start + 1);
        }
    }

});