Ext.define('liftnet.store.common_getdeptlist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'common_getdeptlist',
        fields: [
            {
                name: 'userid'
            },
            {
                name: 'username'
            },
            {
                name: 'storageid'
            },
            {
                name: 'storagename'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/common/api/1.0/ssbmlist';
            var userid = global.getUserId();
            var adapterParm = {userid: userid};

            callPostAdapter(
                url, adapterParm,
                function(ret) {
                    me.setData(ret.data);
                    onSuccess(ret.data.length);
                },
                onFailure);
        } else {

            // 获取司/站别列表-演示数据
            this.setData([
                {
                    userid: 'huangl',
                    username: '黄林',
                    storageid:'130001',
                    storagename:'维保一组'
                },
                {
                    userid: 'huangl',
                    username: '黄林',
                    storageid:'13000222',
                    storagename:'维保二组'
                }

            ]);

            onSuccess();
        }
    }

});