Ext.define('liftnet.store.cu_culist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'cu_culist',
        fields: [
            {
                name: 'custid'
            },
            {
                name: 'custname'
            },
            {
                name: 'r4'
            },
            {
                name: 'r1name'
            },
            {
                name: 'r2'
            },
            {
                name: 'flowusername'
            },
            {
                name: 'r5'
            },
            {
                name: 'address'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm, action) {
        var me = this;
        console.log(parm);
        if(global.isWorklight()) {
            var url = '/adapters/mugcustomer/api/1.0/khlist';
            callPostAdapter(
                url, parm,
                function(ret) {
                    var info;
                    if(ret.status.code!=200){
                        if(parm.searchkey!==''){
                            onFailure('搜索客户维护列表失败');
                            return;
                        }
                        onFailure('读取客户维护列表失败');
                    }else{
                        if(ret.data.length===0){
                            if(parm.curpagenum===1){
                                switch(action){
                                    case 'refresh':
                                        info = '没有客户维护数据';
                                        break;
                                    case 'loadmore':
                                        break;
                                    case 'firstload':
                                        info = '没有客户维护数据';
                                        break;
                                    case 'search':
                                        info = '没有相关客户维护数据';
                                        break;
                                }

                                me.setData(ret.data);
                                onFailure(info);
                            }else{
                                onFailure('没有更多数据！');
                            }


                            return;
                        }

                        //第一次加载直接设置数据
                        if(parm.curpagenum==1){
                            console.log('________'+parm.curpagenum);
                            me.setData(ret.data);
                        }else{
                            //分页用法，在原有数据上继续添加
                            var n = ret.data.length;
                            for(var i=0; i<n; i++) {
                                me.add(ret.data[i]);
                            }
                        }

                        if(action==='loadmore'){
                            onSuccess(ret.data.length,ret.jurisdiction);
                        }else{
                            onSuccess('resetCurPage',ret.jurisdiction);
                        }
                    }
                },
                function() {
                    var info;
                    switch(action){
                        case 'refresh':
                            info = '刷新客户维护数据失败';
                            break;
                        case 'loadmore':
                            info = '加载更多客户维护数据失败';
                            break;
                        case 'firstload':
                            info = '获取客户维护数据失败';
                            break;
                        case 'search':
                            info = '搜索客户维护失败';
                            break;
                    }
                    onFailure(info);
                }
            );
        } else {
            //分页模拟
            var start = (parm.curpagenum-1)*20,
                end = parm.curpagenum * parm.pagesize;

            if(end>60){
                onFailure('没有更多数据！');
                return;
            }

            for(var i=start; i<end; i++) {
                this.add({
                    custid: 'CB1600000023',
                    custname:'广州中梯物联'+(i),
                    address: '广东省广州市荔湾区蓬莱路3-5号.' + (i + 1),
                    r4: '普通客户',
                    r1name: '吴维',
                    r2: '2016-04-22',
                    flowusername:'吴维(维保事业部),李光(维保一组)',
                    r5: '维保公司'

                });
            }
            if(action=='firstload'||action=='refresh'||action=='search'){
                onSuccess('resetCurPage',{
                    addjurisdiction:	'N',	//新建权限（Y/N）,
                    updatejurisdiction:	'Y',	//修改权限（Y/N）
                    deletejueisdiction:	'Y',	//删除权限（Y/N）

                });
                return;
            }

            onSuccess(end - start + 1,{
                    addjurisdiction:	'N',	//新建权限（Y/N）,
                    updatejurisdiction:	'Y',	//修改权限（Y/N）
                    deletejueisdiction:	'Y',	//删除权限（Y/N）

                });

                }
    }

});