Ext.define('liftnet.store.cu_flowuserlist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'cu_flowuserlist',
        fields: [
            {
                name: 'userid'
            },
            {
                name: 'username'
            },
            {
                name: 'class1'
            },
            {
                name: 'storageid'
            },
            {
                name: 'storagename'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        console.log(parm);
        var me = this;
        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/mugcustomer/api/1.0/mugcustomerflowuser';
            callPostAdapter(
                url, parm,
                function(ret) {
                    var d = ret.data;
                    var n = d.length;
                    for(var i=0; i<n; i++) {
                        me.add(d[i]);
                    }
                    onSuccess(n);
                },
            onFailure);
        } else {
            var start = (parm.curpagenum-1)*parm.pagesize+1, end = start + parm.pagesize-1;

            for(var i=start; i<=end; i++) {
                this.add({
                    userid		:		'001-000'+i,
                    username	: 		'测试用户.' + i,
                    class1		: 		'职务.'+i,
                    storageid	:		'130010'+i,
                    storagename	:		'测试部门.'+i
                });
            }
            // 演示数据
            onSuccess(end - start + 1);
        }
    }

});