Ext.define('liftnet.store.cu_levelist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'cu_levelist',
        fields: [
            {
                name: 'levelid'
            },
            {
                name: 'levelname'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/mugcustomer/api/1.0/customerleve';

            console.log('-----传到后台的参数-----');
            console.log(parm);
            console.log('-----传到后台的参数-----');
            callPostAdapter(
                url, parm,
                function(ret) {
                    me.setData(ret.data);
                    console.log(me.getData());
                    onSuccess(ret.data.length);
                },
                onFailure);
        } else {
            this.setData([
                {
                    levelid		: 	'A',
                    levelname	:	'重点客户'
                },
                {
                    levelid		: 	'B',
                    levelname	:	'普通客户'
                },
                {
                    levelid		: 	'C',
                    levelname	:	'非优先客户'
                },
            ]);
            onSuccess(3);
        }
    }

});