Ext.define('liftnet.store.eng_arealist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_arealist',
        fields: [
            {
                name: 'areaid'
            },
            {
                name: 'areaname'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        parm = {userid: global.getUserId(),areatype:parm.areatype};

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/common/api/1.0/szqylist';
            callPostAdapter(
                url, parm,
                function(ret) {
                    me.setData(ret.data);
                    onSuccess(ret.data.length);
                },
                onFailure);
        } else {

            // 获取受信来源列表-演示数据
            this.setData([
                {
                    placeid: '1',
                    placename: '东北'
                },
                {
                    placeid: '2',
                    placename: '华北'
                },
                {
                    placeid: '3',
                    placename: '华东'
                },
                {
                    placeid: '4',
                    placename: '华南'
                },
                {
                    placeid: '5',
                    placename: '华中'
                },
                {
                    placeid: '6',
                    placename: '西北'
                },
                {
                    placeid: '7',
                    placename: '西南'
                },

            ]);
                onSuccess(7);
                }
    }

});