Ext.define('liftnet.store.eng_elevatorlocation', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_elevatorlocation',
        fields: [
            {
                name: 'prono'
            },
            {
                name: 'liftno'
            },
            {
                name: 'elename'
            },
            {
                name: 'projectname'
            },
            {
                name: 'projectaddress'
            },
            {
                name: 'facname'
            },
            {
                name: 'elevatorid'
            },
            {
                name: 'fsd'
            },
            {
                name: 'lon'
            },
            {
                name: 'lat'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        var adapterparm = {
            userid: global.getUserId(),
            searchker:parm.searchker,
            lon:parm.lon,
            lat:parm.lat,
            rangradius:parm.rangradius
        };

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/common/api/1.0/dtdlwzcx';
            callPostAdapter(
                url, adapterparm,
                function(ret) {
                    if(ret.status.code!=200){
                        if(parm.searchker!==''){
                            onFailure('读取电梯位置信息失败');
                        }else{
                            onFailure('搜索电梯位置信息失败');
                        }

                    }else{
                        me.setData(ret.data);
                        onSuccess(me.getData());
                    }
                },
                function() {
                    if(parm.searchker!==''){
                         onFailure('读取电梯位置信息失败');
                    }else{
                         onFailure('搜索电梯位置信息失败');
                    }
                });
        //         function(ret) {
        //             me.setData(ret.data);
        //             onSuccess(me.getData());
        //         },
        //         onFailure);
        } else {

            // 获取电梯位置列表-演示数据
            this.setData([
                {
                    prono:'JST00123',
                    projectname:'projectname1',
                    projectaddress: 'address1',
                    lon:'113.2982940000',
                    lat:'23.1077250000',
                },
                {
                    prono:'JST00456',

                    projectname:'projectname2',
                    projectaddress: 'address2',
                    lon:'113.3231550000',
                    lat:'23.1286300000',
                }



            ]);
                onSuccess(this.getData());
                }
    },

    filteDat: function(parm) {
        //搜索注册码，项目名称，项目地址
        this.clearFilter();
                if(parm!=='') {
                    this.filterBy(function(item) {
                        var s = item.get("prono") + ' ' +
                            item.get("projectname")+' ' +
                            item.get("projectaddress");
                        return (s.toUpperCase().indexOf(parm.toUpperCase())>=0);
                    });
                }
    }

});