Ext.define('liftnet.store.eng_firstcheck_checkpart', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_firstcheck_checkpart',
        fields: [
            {
                name: 'state'
            }
        ]
    },

    isExist: function(onSuccess, onFailure, parm) {
        var me = this;
        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/firstcheck/api/1.0/fccheckparts';
            var adapterparm = {
                userid:global.getUserId(),
                partsid:parm
            };
            callPostAdapter(
                url, adapterparm,
                function(ret){
                    if(ret.status.code!=200){
                        onFailure('校验失败');
                    }else{
                        me.setData(ret.data);
                        onSuccess(ret.data[0].state===0);
        //                 return ret.data[0].state===0;
                    }
                },
                function(){
                    onFailure('校验失败');
        //             return false;
                },
                adapterparm
            );
        }else{
            onSuccess(parm==='123');
        }
    }

});