Ext.define('liftnet.store.eng_liftdtxhlist', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_liftdtxhlist',
        fields: [
            {
                name: 'elevatorid'
            },
            {
                name: 'elevatorname'
            },
            {
                name: 'facid'
            },
            {
                name: 'facname'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        console.log(parm);
        // parm = {userid: global.getUserId(),searchname:parm.searchname};

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/firstcheck/api/1.0/dtxhlist';
            callPostAdapter(
                url, parm,
                function(ret) {
                    var d = ret.data;
                    var n = d.length;
                    for(var i=0; i<n; i++) {
                        me.add(d[i]);
                    }
                    onSuccess(n);
                },
                onFailure);
        } else {

            // 获取电梯型号列表-演示数据
            var start = (parm.curpagenum-1)*parm.pagesize+1, end = start + parm.pagesize-1;

            for(var i=start; i<=end; i++) {
                this.add({
                    elevatorid :'e'+i,
                    elevatorname: '电梯型号'+(i+1),
                    facid: i+1,
                    facname: '电梯品牌' + (i+1),
                });
            }
            // 演示数据
            onSuccess(end - start + 1);


                }
    }

});