Ext.define('liftnet.store.eng_liftglproject', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_liftglproject',
        fields: [
            {
                name: 'detailrowid'
            },
            {
                name: 'r12'
            },
            {
                name: 'r13'
            },
            {
                name: 'projectname'
            },
            {
                name: 'eleuseaddress'
            },
            {
                name: 'ladderno'
            },
            {
                name: 'pordid'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        console.log(parm);
        // parm = {userid: global.getUserId()};

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/common/api/1.0/bycontractpro';
            callPostAdapter(
                url, parm,
                function(ret) {
                    var d = ret.data;
                    var n = d.length;
                    for(var i=0; i<n; i++) {
                        me.add(d[i]);
                    }
                    onSuccess(n);
                },
                onFailure);
        } else {

            // 获取电梯关联项目列表-演示数据
            var start = (parm.curpagenum-1)*parm.pagesize+1, end = start + parm.pagesize-1;

            for(var i=start; i<=end; i++) {
                this.add({

                    detailrowid: 'detail_'+i,//合同明细流水号
                    r12: 'project_'+i,//项目编号
                    r13:'contract_'+i,//合同编号
                    projectname:'测试项目'+i,//项目名称
                    eleuseaddress:'测试项目地址1111111111111111111111_'+i,//项目地址
                    ladderno : 'TH_'+i,//梯号
                    pordid : '1234-123456-123456-12'+i,//设备注册码
                });
            }
            // 演示数据
            onSuccess(end - start + 1);

                }
    }

});