Ext.define('liftnet.store.eng_pointlocation', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_pointlocation',
        fields: [
            {
                name: 'storagename'
            },
            {
                name: 'maintainhead'
            },
            {
                name: 'Maintenanceman'
            },
            {
                name: 'area'
            },
            {
                name: 'lon'
            },
            {
                name: 'lat'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        var adapterparm = {
            userid: global.getUserId(),
            searchker:parm.searchker,
            lon:parm.lon,
            lat:parm.lat,
            rangradius:parm.rangradius
        };

        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/common/api/1.0/zddlwzcx';
            callPostAdapter(
                url, adapterparm,
                function(ret) {
                    if(ret.status.code!=200){
                        if(parm.searchker!==''){
                            onFailure('读取驻点位置信息失败');
                        }else{
                            onFailure('搜索驻点位置信息失败');
                        }

                    }else{
                        me.setData(ret.data);
                        onSuccess(me.getData());
                    }
                },
                function() {
                    if(parm.searchker!==''){
                            onFailure('读取驻点位置信息失败');
                        }else{
                            onFailure('搜索驻点位置信息失败');
                        }
                }
                );
        } else {

            // 获取人员位置列表-演示数据
            this.setData([
                {
                    storagename: 'pointname1',
                    maintainhead: 'maintainhead1',
                    Maintenanceman:'Maintenanceman1',
                    lon:'113.3185990000',
                    lat:'23.1144410000',
                },
                {
                    storagename: 'pointname2',
                    maintainhead: 'maintainhead2',
                    Maintenanceman:'Maintenanceman2',
                    lon:'113.3228900000',
                    lat:'23.1078480000',
                }



            ]);
                onSuccess(this.getData());
                }
    },

    filteDat: function(parm) {
        //搜索 保养站/班组名称、保养站长/班组长姓名、负责区域
        this.clearFilter();
                if(parm!=='') {
                    this.filterBy(function(item) {
                        var s = item.get("storagename") + ' ' +
                            item.get("maintainhead")+' '+
                            item.get("area");
                        return (s.toUpperCase().indexOf(parm.toUpperCase())>=0);
                    });
                }
    }

});