Ext.define('liftnet.store.eng_todotype', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'eng_todotype',
        fields: [
            {
                name: 'type'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        //console.log(JsonUtil.convertToString(parm));
        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/liftnet/common/api/1.0/';
            var adapterparm = {userid:global.getUserId()};
            callPostAdapter(
                url, adapterparm,
                function(ret) {
                    me.setData(ret.data);
                    onSuccess(ret.data.length);
                },
                onFailure);
        } else {

            var d = [
                {type:'保养计划'},
                {type:'维修任务'},
                {type:'故障报告'}
            ];
            this.setData(d);
            onSuccess(d.length);
        }
    }

});