Ext.define('liftnet.store.mainpage_notisfy', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'mainpage_notisfy',
        fields: [
            {
                name: 'Topic',
                type: 'string'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
                var me = this;

                if(global.isWorklight()) {
                    // adapter取数
                    var url = '/adapters/common/api/1.0/announcement';
                    var userid = global.getUserId();
                    var adapterParm = {userid: userid};

                    callPostAdapter(
                        url, adapterParm,
                        function(data) {
                            me.setData(data.data);
                            onSuccess();
                        },
                        onFailure);
                } else {
                    // 演示数据
                    this.setData([
                        {
                            Topic: '系统今晚24点停机维护,请大家做好数据备份。'
        //                     Topic:''
                        }
                    ]);

                    onSuccess(1);
                }

    },

    updateDat: function(onSuccess, onFailure, parm) {

    },

    filteDat: function(parm) {

    }

});