    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/mainpage/api/1.0/static';
            var userid = global.getUserId();
            var adapterParm = {userid:userid};

            callPostAdapter(
                url, adapterParm,
                function(ret) {
                    me.setData(ret.data);
                    onSuccess(ret.data.length);
                },
                onFailure);
        } else {
            // 处理故障、维修保养数量统计-演示数据
            this.setData([
                {
                    bymissioncount:18,
                    jixiucount:88
                }
            ]);

            onSuccess();
        }
    },

