Ext.define('liftnet.store.setting_cuserinfo', {
    extend: 'Ext.data.Store',

    requires: [
        'Ext.data.Field'
    ],

    config: {
        storeId: 'setting_cuserinfo',
        fields: [
            {
                name: 'message',
                type: 'string'
            }
        ]
    },

    loadDat: function(onSuccess, onFailure, parm) {
        var me = this;
        console.log(parm);
        if(global.isWorklight()) {
            // adapter取数
            var url = '/adapters/common/api/1.0/changepassword';

            callPostAdapter(
                url, parm,
                function(ret) {
                    var msg;
                    if(ret.data.length>0) {
                        msg = ret.data[0].message;
                    } else {
        //                 msg = ret.status.info;
                        msg = '修改用户信息失败';
                    }
                    onSuccess(msg);
                },
            onFailure);
        } else {
            onSuccess('密码修改成功！');
        }
    }

});